public class Esercizio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*Scrivere un programma Java per scorrere tutti gli elementi
		  in un elenco di array.*/
		
		int [] elenco = {4,78,19,14,17,28};
		
		System.out.println("Numeri presenti nell'elenco: ");
		
		for(int i = 0; i < elenco.length; i++) {
			System.out.println(elenco[i]);
		}
		
		
	}

}
